import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

// Main container with full height, centered content, and a background image
const HomeContainer = styled.div`
  height: 100vh; // Full viewport height
  display: flex; // Flexbox for centering content
  flex-direction: column;
  align-items: center; // Center horizontally
  justify-content: center; // Center vertically
  background: url('https://source.unsplash.com/1600x900/?medicine,health') center/cover; 
  background-size: cover; // Ensure the image covers the entire container
  color: white;
  text-align: center;
  font-family: 'Poppins', sans-serif;
  padding: 20px;
`;

// Main title with large font size and bold styling
const Title = styled.h1`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 20px;
`;

// Subtitle with a slightly smaller font
const Subtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 30px;
`;

// Button styled with padding, border radius, and a transition effect for hover and active states
const Button = styled.button`
  margin-top: 20px;
  padding: 15px 30px;
  font-size: 18px;
  border: none;
  border-radius: 10px;
  background: #ff6b6b;
  color: white;
  cursor: pointer;
  transition: 0.3s;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);

  &:hover {
    background: #d44f4f;
    transform: scale(1.05);
    box-shadow: 0 6px 8px rgba(0, 0, 0, 0.2);
  }

  &:active {
    transform: scale(0.98);
  }
`;

const Home = () => {
  const navigate = useNavigate();

  return (
    <HomeContainer>
      <Title>Welcome to ADR Detection</Title>
      <Subtitle>Find out adverse drug reactions & discussions with ease.</Subtitle>
      {/* Button that navigates to the ADR search page */}
      <Button onClick={() => navigate('/search')}>Start Searching</Button>
    </HomeContainer>
  );
};

export default Home;
