import React, { useEffect, useState } from "react";
import axios from "axios";
import './App.css';

function App() {
  const [redditData, setRedditData] = useState([]);
  const [adrData, setAdrData] = useState([]);
  const [drugName, setDrugName] = useState("Aspirin");

  // Fetch Reddit Data
  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/reddit_data`)
      .then(response => {
        setRedditData(response.data);
      })
      .catch(error => {
        console.error("Error fetching Reddit data", error);
      });
  }, []);

  // Fetch ADR Data
  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/detect_adr/${drugName}`)
      .then(response => {
        setAdrData(response.data.adr_found);
      })
      .catch(error => {
        console.error("Error fetching ADR data", error);
      });
  }, [drugName]);

  return (
    <div className="App">
      <h1>ADR Detection for {drugName}</h1>
      <div>
        <h2>ADR Detection Results:</h2>
        <ul>
          {adrData.length > 0 ? adrData.map((adr, index) => (
            <li key={index}>{adr}</li>
          )) : <p>No ADRs detected.</p>}
        </ul>
      </div>

      <div>
        <h2>Reddit Data:</h2>
        <ul>
          {redditData.map((post, index) => (
            <li key={index}>
              {post.title} - <a href={post.url} target="_blank" rel="noopener noreferrer">Link</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
