import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

// Styled Components
const HomeContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: url('https://source.unsplash.com/1600x900/?medicine,health') center/cover; 
  background-size: cover;
  color: white;
  text-align: center;
  font-family: 'Poppins', sans-serif;
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 20px;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 30px;
`;

const Button = styled.button`
  margin-top: 20px;
  padding: 15px 30px;
  font-size: 18px;
  border: none;
  border-radius: 10px;
  background: #ff6b6b;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #d44f4f;
    transform: scale(1.05) rotate(5deg);
    box-shadow: 0 6px 8px rgba(0, 0, 0, 0.2);
  }

  &:active {
    transform: scale(0.98);
  }
`;

const CallToAction = styled.div`
  background-color: rgba(0, 0, 0, 0.7);
  padding: 40px;
  margin-top: 40px;
  border-radius: 10px;
  color: white;
`;

const CTAHeader = styled.h2`
  font-size: 2rem;
  margin-bottom: 20px;
`;

const CTAButton = styled.button`
  background-color: #27ae60;
  color: white;
  padding: 10px 20px;
  font-size: 1.2rem;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #2ecc71;
  }
`;

const Footer = styled.footer`
  position: fixed;
  bottom: 0;
  width: 100%;
  padding: 10px;
  background-color: #2c3e50;
  color: white;
  text-align: center;
`;

// Home Component
const Home = () => {
  const navigate = useNavigate();

  return (
    <HomeContainer>
      <Title>Welcome to ADR Detection</Title>
      <Subtitle>Find out adverse drug reactions & discussions with ease.</Subtitle>
      <Button onClick={() => navigate('/search')}>Start Searching</Button>
      
      {/* Call to Action Section */}
      <CallToAction>
        <CTAHeader>Ready to get started?</CTAHeader>
        <CTAButton onClick={() => navigate('/search')}>Start Searching</CTAButton>
      </CallToAction>

      {/* Footer */}
      <Footer>
        <p>&copy; 2025 ADR Detection | All Rights Reserved</p>
      </Footer>
    </HomeContainer>
  );
};

export default Home;
