import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

// Styled Components
const HomeContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  font-family: 'Poppins', sans-serif;
  color: white;
  padding: 20px;
  
  background: url('https://source.unsplash.com/1600x900/?pharmacy,healthcare') center/cover;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.6); /* Dark overlay */
    z-index: 0;
  }
`;

const Content = styled.div`
  position: relative;
  z-index: 1;
  max-width: 600px;
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 20px;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 30px;
  line-height: 1.6;
`;

const Button = styled.button`
  padding: 15px 35px;
  font-size: 1.2rem;
  border: none;
  border-radius: 8px;
  background: #ff6b6b;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 600;

  &:hover {
    background: #d44f4f;
    transform: translateY(-3px);
    box-shadow: 0 8px 16px rgba(255, 107, 107, 0.3);
  }

  &:active {
    transform: scale(0.98);
  }
`;

const CallToAction = styled.div`
  background-color: rgba(255, 255, 255, 0.15);
  padding: 30px;
  margin-top: 40px;
  border-radius: 12px;
  color: white;
  text-align: center;
  backdrop-filter: blur(5px);
`;

const CTAHeader = styled.h2`
  font-size: 2rem;
  margin-bottom: 15px;
`;

const CTAButton = styled(Button)`
  background-color: #27ae60;

  &:hover {
    background-color: #2ecc71;
    box-shadow: 0 8px 16px rgba(39, 174, 96, 0.3);
  }
`;

const Footer = styled.footer`
  position: absolute;
  bottom: 10px;
  width: 100%;
  text-align: center;
  font-size: 0.9rem;
  color: #ddd;
`;

// Home Component
const Home = () => {
  const navigate = useNavigate();

  return (
    <HomeContainer>
      <Content>
        <Title>Welcome to ADR Detection</Title>
        <Subtitle>Find out adverse drug reactions & discussions with ease.</Subtitle>
        <Button onClick={() => navigate('/search')}>Start Searching</Button>
        
        {/* Call to Action Section */}
        <CallToAction>
          <CTAHeader>Ready to get started?</CTAHeader>
          <CTAButton onClick={() => navigate('/search')}>Start Now</CTAButton>
        </CallToAction>
      </Content>

      {/* Footer */}
      <Footer>
        <p>&copy; 2025 ADR Detection | All Rights Reserved</p>
      </Footer>
    </HomeContainer>
  );
};

export default Home;
