import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import QRCode from 'qrcode';
import { motion } from 'framer-motion';

// Styled Components
const HomeContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  font-family: 'Poppins', sans-serif;
  color: white;
  padding: 20px;
  background: url('https://source.unsplash.com/1600x900/?pharmacy,healthcare') center/cover;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.6); /* Dark overlay */
    z-index: 0;
  }
`;

const Content = styled.div`
  position: relative;
  z-index: 1;
  max-width: 600px;
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 20px;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 30px;
  line-height: 1.6;
`;

const Button = styled.button`
  padding: 15px 35px;
  font-size: 1.2rem;
  border: none;
  border-radius: 8px;
  background: #ff6b6b;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 600;

  &:hover {
    background: #d44f4f;
    transform: translateY(-3px);
    box-shadow: 0 8px 16px rgba(255, 107, 107, 0.3);
  }

  &:active {
    transform: scale(0.98);
  }
`;

const CallToAction = styled.div`
  background-color: rgba(255, 255, 255, 0.15);
  padding: 30px;
  margin-top: 40px;
  border-radius: 12px;
  color: white;
  text-align: center;
  backdrop-filter: blur(5px);
`;

const CTAHeader = styled.h2`
  font-size: 2rem;
  margin-bottom: 15px;
`;

const CTAButton = styled(Button)`
  background-color: #27ae60;

  &:hover {
    background-color: #2ecc71;
    box-shadow: 0 8px 16px rgba(39, 174, 96, 0.3);
  }
`;

const Footer = styled.footer`
  position: absolute;
  bottom: 10px;
  width: 100%;
  text-align: center;
  font-size: 0.9rem;
  color: #ddd;

  a {
    color: #27ae60;
    text-decoration: none;
    margin: 0 5px;
  }
`;

const QRCodeContainer = styled.div`
  margin-top: 30px;
  text-align: center;
  max-width: 400px;
`;

const InputField = styled.input`
  padding: 12px 20px;
  font-size: 16px;
  width: 100%;
  border-radius: 8px;
  border: 1px solid #ccc;
  margin-bottom: 20px;
`;

const QRCodeImage = styled.img`
  margin-top: 20px;
  max-width: 100%;
  height: auto;
  border: 2px solid #ddd;
  border-radius: 8px;
`;

const Tooltip = styled.div`
  position: absolute;
  background: #222;
  color: white;
  padding: 10px;
  border-radius: 8px;
  font-size: 0.9rem;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
`;

const AboutSection = styled.section`
  background: #f5f5f5;
  padding: 40px;
  border-radius: 12px;
  margin-top: 40px;
`;

const HelpButton = styled(Button)`
  position: relative;
  background-color: #3498db;
  margin-top: 20px;

  &:hover {
    background-color: #2980b9;
  }
`;

// Home Component
const Home = () => {
  const navigate = useNavigate();
  const [drugInfo, setDrugInfo] = useState('');
  const [qrData, setQrData] = useState('');
  const [loading, setLoading] = useState(false);
  const [drugCategory, setDrugCategory] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [showTooltip, setShowTooltip] = useState(false);

  const handleInputChange = (e) => {
    setDrugInfo(e.target.value);
  };

  const handleCategoryChange = (e) => {
    setDrugCategory(e.target.value);
  };

  const generateQRCode = (data) => {
    if (data) {
      setLoading(true);
      QRCode.toDataURL(data, (err, url) => {
        if (err) {
          console.error('QR Code generation failed:', err);
        } else {
          setQrData(url);
        }
        setLoading(false);
      });
    }
  };

  useEffect(() => {
    if (drugInfo) {
      generateQRCode(`Drug Information: ${drugInfo}`);
    }
  }, [drugInfo]);

  const handleLogin = () => {
    // Simulate a login action
    setLoggedIn(!loggedIn);
  };

  return (
    <HomeContainer>
      <Content>
        <Title>Welcome to ADR Detection</Title>
        <Subtitle>Find out adverse drug reactions & discussions with ease.</Subtitle>
        <Button onClick={() => navigate('/search')}>Start Searching</Button>

        {/* Login Button */}
        <Button onClick={handleLogin}>
          {loggedIn ? 'Logged In' : 'Login'}
        </Button>

        {/* Drug Information Input and QR Code */}
        <QRCodeContainer>
          <InputField
            type="text"
            placeholder="Enter drug information"
            value={drugInfo}
            onChange={handleInputChange}
          />
          <InputField as="select" value={drugCategory} onChange={handleCategoryChange}>
            <option value="">Select Drug Category</option>
            <option value="Antibiotics">Antibiotics</option>
            <option value="Painkillers">Painkillers</option>
            <option value="Vitamins">Vitamins</option>
          </InputField>
          {loading && <p>Loading QR Code...</p>}
          {qrData && (
            <div>
              <h2>QR Code for {drugInfo}</h2>
              <QRCodeImage src={qrData} alt="Generated QR Code" />
            </div>
          )}
        </QRCodeContainer>

        {/* Tooltip */}
        <HelpButton onClick={() => setShowTooltip(!showTooltip)}>
          ? Help
        </HelpButton>
        {showTooltip && <Tooltip>This feature allows you to search for drugs and their adverse reactions.</Tooltip>}

        {/* About Section */}
        <AboutSection>
          <h2>About ADR Detection</h2>
          <p>Our platform helps you easily detect and learn about adverse drug reactions.</p>
        </AboutSection>

        {/* Call to Action Section */}
        <CallToAction>
          <CTAHeader>Ready to get started?</CTAHeader>
          <CTAButton onClick={() => navigate('/search')}>Start Now</CTAButton>
        </CallToAction>
      </Content>

      {/* Footer */}
      <Footer>
        <p>&copy; 2025 ADR Detection | All Rights Reserved</p>
        <p>
          <a href="/privacy-policy">Privacy Policy</a> |{' '}
          <a href="/terms">Terms of Service</a>
        </p>
      </Footer>
    </HomeContainer>
  );
};

export default Home;
