import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const HomeContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: url('https://source.unsplash.com/1600x900/?medicine,health') center/cover;
  color: white;
  text-align: center;
`;

const Button = styled.button`
  margin-top: 20px;
  padding: 15px 30px;
  font-size: 18px;
  border: none;
  border-radius: 10px;
  background: #ff6b6b;
  color: white;
  cursor: pointer;
  transition: 0.3s;

  &:hover {
    background: #d44f4f;
  }
`;

const Home = () => {
  const navigate = useNavigate();

  return (
    <HomeContainer>
      <h1>Welcome to ADR Detection</h1>
      <p>Find out adverse drug reactions & discussions with ease.</p>
      <Button onClick={() => navigate('/search')}>Start Searching</Button>
    </HomeContainer>
  );
};

export default Home;
