import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

// Full-screen container with a background image and a dark overlay
const HomeContainer = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: 
    linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)),
    url('https://source.unsplash.com/1600x900/?medicine,health');
  background-size: cover;
  background-position: center;
  color: #ffffff;
  text-align: center;
  font-family: 'Poppins', sans-serif;
  padding: 20px;
`;

// Main title
const Title = styled.h1`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 20px;
`;

// Subtitle for additional description
const Subtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 30px;
`;

// Styled button with hover and active effects
const Button = styled.button`
  padding: 15px 30px;
  font-size: 1.2rem;
  border: none;
  border-radius: 8px;
  background-color: #ff6b6b;
  color: #ffffff;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background-color: #d44f4f;
    transform: scale(1.05);
  }
  
  &:active {
    transform: scale(0.95);
  }
`;

const Home = () => {
  const navigate = useNavigate();

  return (
    <HomeContainer>
      <Title>ADR Detection from Reddit</Title>
      <Subtitle>Welcome to ADR Detection</Subtitle>
      <Subtitle>Find out adverse drug reactions & discussions with ease.</Subtitle>
      <Button onClick={() => navigate('/search')}>Start Searching</Button>
    </HomeContainer>
  );
};

export default Home;
