import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  text-align: center;
  background: linear-gradient(to right, #6a11cb, #2575fc);
  color: white;
  padding: 20px;
`;

const Box = styled.div`
  background: white;
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  width: 50%;
  text-align: center;
  color: black;
`;

const Input = styled.input`
  width: 80%;
  padding: 12px;
  margin-top: 10px;
  font-size: 18px;
  border-radius: 5px;
  border: 1px solid #ccc;
`;

const Button = styled.button`
  margin-top: 15px;
  padding: 12px 18px;
  font-size: 18px;
  background: #ff4b2b;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.3s;

  &:hover {
    background: #ff3a1f;
  }
`;

const PostContainer = styled.div`
  background: #f9f9f9;
  padding: 15px;
  margin-top: 15px;
  border-radius: 8px;
  border-left: 5px solid #ff4b2b;
  text-align: left;
`;

const ADRDetection = () => {
  const [drugName, setDrugName] = useState('');
  const [adrResults, setAdrResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searched, setSearched] = useState(false);

  const fetchADRData = async () => {
    if (!drugName.trim()) return;
    setLoading(true);
    setError(null);
    setSearched(true);

    try {
      const response = await fetch(`http://localhost:5000/get-adr-results?drug_name=${drugName}`);
      const data = await response.json();

      if (data.error) {
        setError(data.error);
        setAdrResults([]);
      } else {
        setAdrResults(data.adr_results || []);
      }
    } catch (err) {
      setError('Failed to fetch ADR data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      {!searched ? (
        <Box>
          <h1>Welcome to ADR Detection</h1>
          <p>Enter the drug name below to check for adverse drug reactions.</p>
          <Input
            type="text"
            placeholder="Enter drug name"
            value={drugName}
            onChange={(e) => setDrugName(e.target.value)}
          />
          <Button onClick={fetchADRData}>Search</Button>
        </Box>
      ) : (
        <Box>
          <h1>Results for {drugName}</h1>
          <Input
            type="text"
            placeholder="Enter drug name"
            value={drugName}
            onChange={(e) => setDrugName(e.target.value)}
          />
          <Button onClick={fetchADRData}>Search Again</Button>

          {loading && <p>Loading...</p>}
          {error && <p style={{ color: 'red' }}>{error}</p>}

          {adrResults.length > 0 ? (
            adrResults.map((result, index) => (
              <PostContainer key={index}>
                <h3>{result.title}</h3>
                <p>{result.selftext}</p>
                <h4>Top Comments:</h4>
                <ul>
                  {result.comments.length > 0 ? (
                    result.comments.map((comment, i) => <li key={i}>{comment}</li>)
                  ) : (
                    <p>No useful comments found.</p>
                  )}
                </ul>
              </PostContainer>
            ))
          ) : (
            <p>No ADRs detected for this drug.</p>
          )}
        </Box>
      )}
    </Container>
  );
};

export default ADRDetection;
