import React, { useState, useEffect } from "react";
import styled from "styled-components";

// Container for the entire ADR detection page
const ADRContainer = styled.div`
  padding: 30px;
  font-family: "Poppins", sans-serif;
  background-color: #f8f8f8;
  min-height: 100vh;
`;

// Title of the ADR detection page
const Title = styled.h1`
  font-size: 2.5rem;
  color: #333;
  text-align: center;
  margin-bottom: 40px;
`;

// Individual ADR item (post) container
const ADRItem = styled.div`
  background-color: #fff;
  padding: 20px;
  margin-bottom: 20px;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

// Title for each ADR post
const ADRTitle = styled.h3`
  font-size: 1.8rem;
  color: #2a2a2a;
`;

// Text of the ADR post
const ADRText = styled.p`
  font-size: 1.2rem;
  color: #555;
  line-height: 1.6;
`;

// List for comments under each ADR post
const CommentList = styled.ul`
  margin-top: 10px;
  list-style-type: none;
  padding-left: 20px;
`;

// Individual comment
const CommentItem = styled.li`
  font-size: 1.1rem;
  color: #444;
  padding: 5px 0;
  border-left: 3px solid #ff6b6b;
  padding-left: 10px;
`;

// Loading and message styling
const Message = styled.p`
  font-size: 1.5rem;
  color: ${({ error }) => (error ? "#ff6b6b" : "#555")};
  text-align: center;
  margin-top: 20px;
`;

const ADRDetection = ({ drugName }) => {
  const [adrResults, setResults] = useState([]);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchADRData = async () => {
      if (!drugName.trim()) {
        setMessage("⚠️ Please provide a valid drug name.");
        return;
      }

      setLoading(true);
      setMessage("");

      try {
        const response = await fetch(`/get-adr-results?drug_name=${encodeURIComponent(drugName)}`);
        const data = await response.json();

        if (!data.adr_results || data.adr_results.length === 0) {
          setMessage("❌ No ADRs detected for this drug.");
          setResults([]);
        } else {
          const hasADR = data.adr_results.some((post) => post.comments.length > 0);
          if (hasADR) {
            setResults(data.adr_results);
            setMessage(""); // Clear previous message
          } else {
            setMessage("ℹ️ No ADRs detected for this drug.");
            setResults([]);
          }
        }
      } catch (error) {
        setMessage("⚠️ Failed to fetch ADR data. Please try again.");
        console.error(error);
      }
      setLoading(false);
    };

    fetchADRData();
  }, [drugName]);

  return (
    <ADRContainer>
      <Title>ADR Detection Results for {drugName}</Title>
      {loading && <Message>⏳ Loading...</Message>}
      {message && <Message error={message.includes("Failed") || message.includes("No ADRs")}>{message}</Message>}
      {adrResults.length > 0 &&
        adrResults.map((post, index) => (
          <ADRItem key={index}>
            <ADRTitle>{post.title}</ADRTitle>
            <ADRText>{post.selftext}</ADRText>
            <h4>Comments:</h4>
            <CommentList>
              {post.comments.map((commentObj, commentIndex) => (
                <CommentItem key={commentIndex}>
                  "{commentObj.comment}" <strong>({commentObj.sentiment})</strong>
                </CommentItem>
              ))}
            </CommentList>
          </ADRItem>
        ))}
    </ADRContainer>
  );
};

export default ADRDetection;
